import React, { useState } from 'react'
import auth0 from '../lib/auth0'

const CreateStatus = () => {
    const [dados, setDados] = useState({
        status: 'bem',
        coords:{
            lat: null,
            long: null
        }
    })
    const getMyLocation = () => {
        if ( navigator.geolocation ) {
            navigator.geolocation.getCurrentPosition(
                position => {
                    console.log(position.coords)
                }
            )
        }
    }
    return (
        <div>
            <h1>Create Status</h1>
            <label className='block'>
                <input type='radio' name='status' value='bem' />
                Estou bem.
            </label>
            <label className='block'>
                <input type='radio' name='status' value='gripe' />
                Estou com sintomas de Gripe/Resfriado.
            </label>
            <label className='block'>
                <input type='radio' name='status' value='covid19' />
                Estou com sintomas do COVID19.
            </label>
            <h2>Sua posição atual: {JSON.stringify(dados)}</h2>
            <button
                className='py-4 px-2 rounded bg-pink-800 font-bold shadow-xl hover:shadow block w-1/4 text-center text-white mx-auto'
                onClick={getMyLocation}
            >
                Minha Posição
            </button>
        </div>
    )
}

export default CreateStatus

export async function getServerSideProps({ req, res }){
    const session = await auth0.getSession(req)

    if ( session ) {

        return {
            props: {
                isAuth: true,
                user: session.user
            }
        }
    }
    return {
        props: {
            isAuth: false,
            user: {}
        }
    }
}
